elementClick.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));

openStateMenuDialog();

function openStateMenuDialog() {
    customDialog.customDialog(htmlTemplate, StateMenuDialogController).subscribe();
}

function StateMenuDialogController(instance) {
    let vm = instance;

    vm.openSubmenu = null;

    vm.toggleSubmenu = function(name) {
        vm.openSubmenu = vm.openSubmenu === name ? null : name;
    };

    vm.isOpen = function(name) {
        return vm.openSubmenu === name;
    };

    vm.goToState = function(state) {
        var params = {};
        if (state === "heatmap") {
            params = {
                entityId: widgetContext.datasources[0].entity.id,
                entityName: widgetContext.datasources[0].entity.name
            };
        }
        else if (state === "reporte_graficos") {
            params = {
                entityId: widgetContext.datasources[1].entity.id,
                entityName: widgetContext.datasources[1].entity.name
            };
        }
        else {
            params = {
                yourParam1: 'value1',
                yourParam2: 'value2'
            };
        }

        widgetContext.stateController.updateState(state, params, false);
        vm.dialogRef.close();
    };

    vm.cancel = function () {
        vm.dialogRef.close();
    };
}

